import React, { useEffect, useState } from "react";
import facebookIcon from "../icons/facebook.png";
import instagramIcon from "../icons/instagram.png";
import twitterIcon from "../icons/twitter.png";
import telegramIcon from "../icons/telegram.png";
import redditIcon from "../icons/reddit.png";
import { useParams } from "react-router-dom";
import { Line } from "react-chartjs-2";
import { Chart, registerables } from "chart.js";
import { currencySymbolMap } from "currency-symbol-map";

/**
 * Display details about specific exchange
 */
function Details(props) {
  /**  exchange contains details about certain exchange */
  const { currency } = props;
  const [coin, setCoin] = useState({});
  const { id } = useParams();
  const SI = ["", "k", "M", "B", "T", "P", "E"];
  Chart.register(...registerables);

  /** fetch details only once for specific exchange */
  useEffect(() => {
    fetch(`https://api.coingecko.com/api/v3/coins/${id}`)
      .then((response) => response.json())
      .then((data) => {
        setCoin(data);
      });
  }, []);

  function formatNumber(number) {
    // what tier? (determines SI symbol)
    const tier = Math.log10(Math.abs(number)) / 3 | 0;

    // if zero, we don't need a suffix
    if(tier == 0) return number;

    // get suffix and determine scale
    const suffix = SI[tier];
    const scale = Math.pow(10, tier * 3);

    // scale the number
    const scaled = number / scale;

    // format number and add suffix
    return scaled.toFixed(1) + suffix;
  }

  if (! coin.market_data) {
    return <div />;
  }

  const { current_price, price_change_24h_in_currency, market_cap } = coin.market_data;
  console.log(coin.market_data);

  return (
    <>    
    <section className="section-table">
      <div className="section-left">
        <Line 
          datasetIdKey="id"
          data={{
            labels: ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec'],
            datasets: [
              {
                id: 1,
                label: coin.name,
                data: [
                  // coin.market_data.current_price.currency - coin.market_data.price_change_percentage_1y_in_currency.currency, 
                  // coin.market_data.current_price.currency
                ],
              }
            ]
          }}
        />
      </div>
    </section>
    <section className="section-table">
      <div className="section-left">
        <div className="section-info">
          <h4>Market Stats</h4>
          <div>
            <div>Market Cap</div>
            <div>Volume(24h)</div>
            <div>Circulating supply</div>
            <div>Typical hold time</div>
          </div>
          <div>
            <div>{`${currencySymbolMap[currency]}${formatNumber(market_cap[currency.toLowerCase()])}`}</div>
            <div>Volume(24h)</div>
            <div>Circulating supply</div>
            <div>Typical hold time</div>
          </div>
        </div>
      </div>
    </section>
    </>
  );
}

export default Details;
