import React, { useEffect, useState } from "react";
import facebookIcon from "../icons/facebook.png";
import instagramIcon from "../icons/instagram.png";
import twitterIcon from "../icons/twitter.png";
import telegramIcon from "../icons/telegram.png";
import redditIcon from "../icons/reddit.png";
import { useParams } from "react-router-dom";
import { Line } from "react-chartjs-2";
import { Chart, registerables } from "chart.js";

/**
 * Display details about specific exchange
 */
function Details(props) {
  /**  exchange contains details about certain exchange */
  const { currency } = props;
  const [coin, setCoin] = useState({});
  const { id } = useParams();
  Chart.register(...registerables);

  /** fetch details only once for specific exchange */
  useEffect(() => {
    fetch(`https://api.coingecko.com/api/v3/coins/${id}`)
      .then((response) => response.json())
      .then((data) => {
        setCoin(data);
      });
  }, []);

  const { current_price, price_change_24h_in_currency } = coin;
  console.log(current_price, price_change_24h_in_currency);  
  return (
    <section className="section-table">
      <div className="details">
        <Line 
          datasetIdKey="id"
          data={{
            labels: ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec'],
            datasets: [
              {
                id: 1,
                label: coin.name,
                data: [
                  // coin.market_data.current_price.currency - coin.market_data.price_change_percentage_1y_in_currency.currency, 
                  // coin.market_data.current_price.currency
                ],
              }
            ]
          }}
        />
      </div>
    </section>
  );
}

export default Details;
