import "./App.css";
import React, { useEffect, useState } from "react";
import DisplayPage from "./components/DisplayPage";
import Details from "./components/Details";
import {
  BrowserRouter as Router,
  Routes,
  Route
} from "react-router-dom";
import { createBrowserHistory } from "history";
import Header from "./components/Header";
import data from './data.json';

/**
 * Main application component 
 */
function App() {
  // Api request result will be saved for exchanges
  const [exchanges, setExchange] = useState([]);
  const [currency, setCurrency] = useState('USD');

  // useEffect(() => {
  //   // request data only once, when first rendered 
  //   fetch("https://api.coingecko.com/api/v3/exchanges")
  //     .then((response) => response.json())
  //     .then((data) => {
  //       setExchange(data.slice(0, 10));
  //     });
  // }, []);

    useEffect(() => {
      fetch("https://ipapi.co/currency")
      .then((response) => setCurrency(response));
    }, []);

  return (
    <>
      <Header />
      <div style={{height: '50px'}}></div>
      <Router history={createBrowserHistory()}>
        <Routes>
          <Route exact path="/" element={<DisplayPage data={data.slice(0, 10)} currency={currency} />} />

          <Route exact path="/details/:id" element={<Details />} />

          <Route exact path="/about" element={<Details />} />
        </Routes>
      </Router>
    </>
  );
}

export default App;
