import React from "react";
import { Table } from "react-bootstrap";
import { Link } from "react-router-dom";
import { Card } from "react-bootstrap";
import { Chart } from "chart.js";

/**
 * Display home page, list of exchanges 
 */
function DisplayPage(props) {
  /** data contains list of exchanges, passed from App.js */
  const { data, currency } = props;

  new Chart('myChart', {
    type: 'line',
    data: {
        labels: ['Red', 'Blue', 'Yellow', 'Green', 'Purple', 'Orange'],
        datasets: [{
            label: '# of Votes',
            data: [12, 19, 3, 5, 2, 3],
            backgroundColor: [
                'rgba(255, 99, 132, 0.2)',
                'rgba(54, 162, 235, 0.2)',
                'rgba(255, 206, 86, 0.2)',
                'rgba(75, 192, 192, 0.2)',
                'rgba(153, 102, 255, 0.2)',
                'rgba(255, 159, 64, 0.2)'
            ],
            borderColor: [
                'rgba(255, 99, 132, 1)',
                'rgba(54, 162, 235, 1)',
                'rgba(255, 206, 86, 1)',
                'rgba(75, 192, 192, 1)',
                'rgba(153, 102, 255, 1)',
                'rgba(255, 159, 64, 1)'
            ],
            borderWidth: 1
        }]
    },
    options: {
        scales: {
            y: {
                beginAtZero: true
            }
        }
    }
});

  return (
      <section>        
        <Table >
          <thead>
            <tr>
              <th className="center">#</th>
              <th className="align-left">Name</th>
              <th className="align-left">Price</th>
              <th className="align-left">Change</th>
              <th className="align-left">Chart</th>
              <th className="align-left">Trade</th>
            </tr>
          </thead>
          <tbody>
            {Object.keys(data).map((index) => (
              <tr key={data[index].id}>
                <td className="center">{data[index].market_cap_rank}</td>
                <td className="align-left">
                  <img style={{ width: "30px" }} src={data[index].image} />
                  <a href={`/details/${data[index].id}`} style={{textDecoration: 'unset'}}>
                    <span className="name">
                        {`${data[index].name} ${data[index].symbol.toUpperCase()}`}                    
                    </span>
                  </a>                  
                </td>
                <td className="align-left"><span className="price">{`${currency} ${Number.parseFloat(data[index].current_price).toFixed(2)}`}</span></td>
                <td className="align-left"><span className="percentage">{`${Number.parseFloat(data[index].price_change_percentage_24h).toFixed(2)}%`}</span></td>
                <td className="align-left"><canvas id="myChart" width="400" height="400"></canvas></td>
                <td className="align-left"><button>Buy</button></td>
              </tr>
            ))}
          </tbody>
        </Table>
      </section>
  );
}

export default DisplayPage;
